instructions = [
  "ULL",
  "RRDDD",
  "LURDL",
  "UUUUD"
] # ok

instructions = [
"",
"",
"",
"",
""
]

class KeyPad
  def initialize
    @horizontal = 0
    @vertical = 0
  end

  def get_key
    if @vertical == 1 and @horizontal == -1
      return 1
    elsif @vertical == 1 and @horizontal == 0
      return 2
    elsif @vertical == 1 and @horizontal == 1
      return 3
    elsif @vertical == 0 and @horizontal == -1
      return 4
    elsif @vertical == 0 and @horizontal == 0
      return 5
    elsif @vertical == 0 and @horizontal == 1
      return 6
    elsif @vertical == -1 and @horizontal == -1
      return 7
    elsif @vertical == -1 and @horizontal == 0
      return 8
    else
      return 9
    end
  end

  def find_key(movements)
    movements.each_char do |move|
      case move
      when "R"
        @horizontal += 1 if @horizontal < 1
      when "L"
        @horizontal -= 1 if @horizontal > -1
      when "U"
        @vertical += 1 if @vertical < 1
      when "D"
        @vertical -= 1 if @vertical > -1
      end
    end
  end

end

keypad = KeyPad.new

instructions.each do |instruction|
  keypad.find_key(instruction)
  puts keypad.get_key
end




